<style>
    table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 20px;
        border: 1px solid black;
    }
    th, td {
        border: 1px solid #ddd;
        padding: 8px;
        text-align: left;
        border: 1px solid black;
    }
    th {
        background-color: #f4f4f4;
        font-weight: bold;
    }
    tbody tr:nth-child(even) {
        background-color: #f9f9f9;
    }
    h3 {
        border-bottom: 2px solid #333;
        padding-bottom: 5px;
        margin-bottom: 15px;
        font-size: 16px;
    }
</style>

{% set subjects = doc.subjects %}
{% if subjects %}
    {% set subjects_list = subjects.split(',') %}
{% else %}
    {% set subjects_list = [] %}
{% endif %}

{% if subjects_list %}
    {% set results = frappe.get_all('Student Results',
        filters={'student_id': doc.name, 'exam_name': ['in', subjects_list]},
        fields=['exam_name', 'student_name', 'student_mobile', 'exam_date', 'rank', 'total_marks', 'total_right', 'total_wrong', 'total_skip', 'percentage', 'exam_title_name','rank_color'],
        order_by='exam_date desc',
    ) %}
{% else %}
    {% set results = frappe.get_all('Student Results',
        filters={'student_id': doc.name},
        fields=['exam_name', 'student_name', 'student_mobile', 'exam_date', 'rank', 'total_marks', 'total_right', 'total_wrong', 'total_skip', 'percentage', 'exam_title_name','rank_color'],
        order_by='exam_date desc',
    ) %}
{% endif %}

{% set exams = {} %}
{% for record in results %}
    {% if record.exam_name not in exams %}
        {% set _ = exams.update({ record.exam_name: [] }) %}
    {% endif %}
    {% set _ = exams[record.exam_name].append(record) %}
{% endfor %}
{% set color_dict = {'G': 'green', 'Y': 'yellow', 'R': 'red'} %}

{% if exams %}
    {% for exam, records in exams.items() %}
        <h3>{{ exam }}</h3>
        <table style="font-size: 16px;">
            <thead>
                <tr>
                    <th style="width: 30%; font-weight: bold; color: black;">Exam Title</th>
                    <th style="width: 15%; font-weight: bold; color: black;">Exam Date</th>
                    <th style="width: 5%; font-weight: bold; color: black;" >Rank</th>
                    <th style="width: 10%; font-weight: bold; color: black;">Total Marks</th>
                    <th style="width: 10%; font-weight: bold; color: black;">Total Right</th>
                    <th style="width: 10%; font-weight: bold; color: black;">Total Wrong</th>
                    <th style="width: 10%; font-weight: bold; color: black;">Total Skip</th>
                    <th style="width: 10%; font-weight: bold; color: black;">Percentage</th>
                </tr>
            </thead>
            <tbody>
                {% for record in records %}
                <tr>
                    <td>{{ record.exam_title_name }}</td>
                    {% set exm_date = record.exam_date|str %}
                    <td>{{ exm_date[-2:] }}-{{ exm_date[5:7] }}-{{ exm_date[:4] }}</td>
                    {% if record.rank_color %}    
                        <td style="background-color: {{ color_dict.get(record.rank_color, 'transparent') }};font-weight: bold;">
                            {{ record.rank }}
                        </td>
                    {% else %}
                        <td>{{ record.rank }}</td>
                    {% endif %}
                    <td>{{ record.total_marks }}</td>
                    <td>{{ record.total_right }}</td>
                    <td>{{ record.total_wrong }}</td>
                    <td>{{ record.total_skip }}</td>
                    <td>{{ record.percentage }}</td>
                </tr>
                {% endfor %}
            </tbody>
        </table>
{% endfor %}
{% else %}
    <p>No records found for the selected subjects or all exams.</p>
{% endif %}"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": ""}]
